mapper.select:
select *
from photos
where p_id=#{p_id}

select *
from size_products
where p_id=#{p_id}

mapper.update:
update products set p_id=#{p_id}, p_name=#{p_name}, p_price=#{p_price}, p_stock=#{p_stock}, p_salescount=#{p_salescount}, p_category_name=#{p_category_name},
p_upload_date=sysdate, p_description=#{p_description}
where p_id=#{p_id}

mapper.select:
select p_id, p_name,p_rate,p_price, p_stock, p_category_name, p_description
from products
where p_id=#{pid}


select rnum, p_id,p_name,p_rate,p_price,p_category_name, p_upload_date,p_stock, photo_sname, photo_type
  from (
      select rownum as rnum,p_id, p_name,p_rate,p_price,p_category_name, p_upload_date,p_stock, photo_sname, photo_type
      from (
           select p.p_id,p.p_name,p.p_rate,p.p_price,p.p_category_name, p.p_upload_date,p.p_stock, ph.photo_sname,ph.photo_type 
          from products p left Join photos ph
          on p.p_id = ph.p_id and
          ph.photo_role='main'
       where p.p_enabled=1

          order by p.p_id desc


          order by p.p_stock


      )
      where rownum <= #{pager.endRowNo}
  )
  where rnum >= #{pager.startRowNo}


select rnum, p_id,p_name,p_rate,p_price, photo_sname, photo_type
 from (
     select rownum as rnum,p_id, p_name,p_rate,p_price, photo_sname, photo_type
     from (
          select p.p_id,p.p_name,p.p_rate,p.p_price, ph.photo_sname,ph.photo_type 
         from products p Join photos ph
         on p.p_id = ph.p_id and
         ph.photo_role='main'
     )
     where rownum <= #{endRowNo}
 )
 where rnum >= #{startRowNo}

select count(*)
  from( select *
      from(select *
       from products
       order by p_salescount desc
      )
  where ROWNUM<31
       )
  where p_category_name=#{category}

select rnum, p_id,p_name,p_rate,p_price, photo_sname, photo_type
   from (
       select rownum as rnum, p_id,p_name,p_rate,p_price, photo_sname, photo_type
       from (
               select p.p_id,p.p_name,p.p_rate,p.p_price, ph.photo_sname,ph.photo_type 
          from products p Join photos ph
          on p.p_id = ph.p_id and
          ph.photo_role='main'
          and p.p_category_name=#{category}
          order by p_upload_date desc
       )
       where rownum <= #{pager.endRowNo}
   )
   where rnum >= #{pager.startRowNo}

select count(*)
from products
where p_category_name=#{category}

select rnum, p_id,p_name,p_rate,p_price, photo_sname, photo_type
   from (
       select rownum as rnum, p_id,p_name,p_rate,p_price, photo_sname, photo_type
       from (
               select p.p_id,p.p_name,p.p_rate,p.p_price, ph.photo_sname,ph.photo_type 
          from products p Join photos ph
          on p.p_id = ph.p_id and
          ph.photo_role='main'
          and p.p_name like '%'||#{searchword}||'%'
          order by p_upload_date desc
       )
       where rownum <= #{pager.endRowNo}
   )
   where rnum >= #{pager.startRowNo}

select rnum, p_id,p_name,p_rate,p_price, photo_sname, photo_type
 from (
       select rownum as rnum, p_id,p_name,p_rate,p_price, photo_sname, photo_type
       from (
               select p.p_id,p.p_name,p.p_rate,p.p_price, ph.photo_sname,ph.photo_type 
          from products p Join photos ph
          on p.p_id = ph.p_id and
          ph.photo_role='main'
          and p.p_name like '%'||#{searchword}||'%' and p_category_name=#{category}
          order by p_upload_date desc
       )
       where rownum <= #{pager.endRowNo}
   )
   where rnum >= #{pager.startRowNo}

select count(*)
from products
where p_name like '%'||#{searchword}||'%' and p_category_name=#{category}

   select *
from products
where p_id=#{pid}
