mapper.select:

select user_id
from users 
where user_name=#{user_name} and user_phone=#{user_phone}


select user_password
from users 
where user_name=#{user_name}

mapper.update:
update users 
set user_password=#{user_password} 
where user_id=#{user_id}

update users 
set user_phone=#{user_phone} 
where user_id=#{user_id}

